Ext.define('Domino.view.BrowsePanelViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.browsepanel',

    onBrowseDetailSelect: function(rowmodel, record, index, eOpts) {
        var me = this;
        // Ã¨ necessario passare un oggetto arbitrario rec (o altro nome) per poterlo puntare dal binding
        me.view.down("#leftDetailPanel").up('#browsePanel').getViewModel().setData({rec:record});


    },

    onMenuitemClick: function(item, e, eOpts) {


        var i = item.up("#browsePanel");
        var store = i.viewModel.storeInfo.SubjectsListStore;
        var proxy = store.proxy;
        proxy.url  ="data/SoggettiMaschi.json";
        store.load();
    },

    onMenuitemClick1: function(item, e, eOpts) {
        var i = item.up("#browsePanel");
        var store = i.viewModel.storeInfo.SubjectsListStore;
        var proxy = store.proxy;
        proxy.url  ="data/SoggettiFemmine.json";
        store.load();
    }

});
